// Parse Ollama responses and format for output
const items = $input.all();
const results = [];

for (const item of items) {
  if (item.json.response) {
    results.push({
      json: {
        content: item.json.response,
        type: item.json.type || 'unknown',
        generatedAt: new Date().toISOString()
      }
    });
  }
}

return results;
